// this is a placeholder object...

XT.Cache = XT.Object.create({

  /** @private */
  init: function() {},

  /** @private */
  insert: function(elem) {

    // retrieve a uuid for the element
    var uuid = XT.Tools.crypto.uuid();

    // call the super's set with the uuid as the path
    this.__super.set.call(this, uuid, elem);
    
    // return the uuid so it can be used to reference the element
    return uuid; 
  },

  /** @private */
  fetch: function(uuid) {

    // wrapper for get
    return this.get(uuid);
  },

  /** @private */
  xtClass: "XT.Cache",

});
