
// require some core modules
var UTIL    = require("util");
var PATH    = require("path");

// load into module namespace from above
require("tools");
require("exception");
require("store");

XT.Record = XT.Object.extend({

  /** @private */
  init: function() {},

  /** @private */
  definition: null,

  /** @private */
  xtClass: "XT.Record",

});

//......................................
// initialization helpers

/** @public */
XT.Record.toMany = function(hash) {
  return XT.Store.recordManager.registerRelation(
    hash, "toMany"); 
};

/** @public */
XT.Record.toOne = function(hash) {
  return XT.Store.recordManager.registerRelation(
    hash, "toOne");
};

/** @public */
XT.Record.static = function(hash) {
  
  // @todo This needs to be full capable but for now...
  var keys = XT.Tools.keys(hash);

  // @note Expecting only 1 entry for known models at this phase
  return "{0}={1}".format(keys[ 0 ], hash[ keys[ 0 ] ]);
};
