
// require some core modules
var UTIL    = require("util");
var PATH    = require("path");

/** @public */
String.prototype.format = function() {
  var args = arguments;
  return this.replace(/{(\d+)}/g, function(match, number) {
    return typeof args[ number ] != "undefined"
    ? args[ number ]
    : "{" + number + "}"
    ;
  });
};

/** @public */
String.prototype.capitalize = function() {
  return this.charAt(0).toUpperCase()+this.slice(1);
};

/** @public */
String.prototype.trim = function() {
  return this.replace(/^\s\s*/, '').replace(/\s\s*$/, '');
};

/** @public */
String.prototype.prepend = function(prefix) {
  return prefix + this;
};

/** @public */
Array.prototype.wrap = function(template, into) {
  var ret = into
    ? into
    : this
    ;
  for(var i=0, len=this.length; i<len; ++i)
    ret[ i ] = template.format(this[ i ]);
  return ret;
};

/** @private */
XT = {};

/** @private */
XT.Tools = {};

/** @public */
XT.Tools.scTypeFromFilename = function(filename) {
  return PATH.basename(filename, ".js")
    .capitalize().prepend("XT.").replace(/_([a-z])/g, 
      function(str) { return str.slice(1).toUpperCase(); });
};

