
// require core modules
var UTIL    = require("util");
var PATH    = require("path");




/** @public */
XT.puts = function(message, buffer) {
  if(buffer) {
    process.stdout.write(message);
  } else { UTIL.puts(message); }
};

XT.puts.end = function() { process.stdout.write("\n"); };

/** @public */
XT.inspect = function(object, returns, min) {
  var data;
  if(min) {
    data = UTIL.inspect(object, false, 1);
  } else {
    data = UTIL.inspect(object, false, 3);
  }
  if(returns) return data;
  UTIL.puts(data);
};


