
//var sys = require('sys');
var U = require("util");

require("tools");

// defined options for command line
XT.opts = {
  port: {
    string: "-p NUM, --port=NUM",
    help: "Port to use to communicate with remote Postgres server",
    callback: function(num) {
      if(isNaN(parseInt(num))) {
        return "Port must be an integer value";
      }
    },
    default: 7000,
  },
  database: {
    string: "-d DATABASE, --database=DATABASE",
    help: "Name of database to use",
    default: "dev",
  },
  user: {
    string: "-u USER, --user=USER",
    help: "Database username to login as",
    required: true,
  },
  host: {
    string: "-H HOST, --host=HOST",
    help: "Host/IP Address to connect to",
    default: "dev.openmfg.com",
  },
  password: {
    string: "-P PASSWORD, --password=PASSWORD",
    help: "The password for the database user",
    required: true,
  },
  debug: {
    string: "-D LEVEL, --debug=LEVEL",
    help: "The debug output level",
    callback: function(debug) {
      if(debug !== parseInt(debug))
        return "Debug `level` must be an integer";
    },
  },
  "server-port": {
    string: "-b PORT, --server-port=PORT",
    help: "The port for the HTTP server to listen on [8080]",
    default: "8080"
  },
  "metasql-server": {
    string: "-M URL/IP, --metasql-server=URL/IP",
    help: "The metasql proxy server URL or IP address",
    default: "sproutcore.xtuple.org",
  },
  "metasql-server-port": {
    string: "-m PORT, --metasql-server-port=PORT",
    help: "The port for the metasql proxy server",
    default: 80,
  },
};

